style:
.exponea-popup-banner {
    box-sizing: border-box;
    display: block;
    position: fixed;
    bottom: 15px;
    right: -360px;
    max-width: 370px;
    z-index: 9999999;
    background: [[ backgroundColor]];
    transition: right 1.5s;
    font-family: Arial, Helvetica, sans-serif;
    text-align: center;
    line-height: normal;
    text-decoration: inherit;
    cursor: pointer;
    padding: 40px;
}

.exponea-popup-banner:hover,
.exponea-popup-banner:active {
    text-decoration: inherit;
}

.exponea-popup-banner.exponea-animate {
    right: 15px;
}

.exponea-popup-banner span {
    display: block;
}

.exponea-popup-banner .exponea-header {
    font-size: 10px;
    font-weight: bold;
    text-transform: uppercase;
    color: [[ headerTextColor]];
    padding: 15px;
}

.exponea-popup-banner .exponea-text {
    font-size: 24px;
    font-weight: 600;
    color: [[ bodyTextColor]];
    margin-bottom: 32px
}

.exponea-popup-banner .exponea-button {
    display: inline-block;
    font-size: 14px;
    color: [[ buttonTextColor]];
    background: [[ buttonBackgroundColor]];
    padding: 13px 37px;
    border-radius: 50px;
    -moz-border-radius: 50px;
    -webkit-border-radius: 50px;
    border: 0px solid #800000;
    cursor: pointer;
}

.exponea-popup-banner .exponea-close {
    padding: 15px;
    position: absolute;
    top: 0px;
    right: 0px;
}

.exponea-popup-banner .exponea-close .exponea-close-cross {
    position: absolute;
    display: inline-block;
    width: 14px;
    height: 14px;
    overflow: hidden;
    top: 8px;
    right: 8px;
}

.exponea-popup-banner .exponea-close:hover {
    cursor: pointer;
}

.exponea-popup-banner .exponea-close .exponea-close-cross:before,
.exponea-popup-banner .exponea-close .exponea-close-cross:after {
    content: '';
    position: absolute;
    height: 2px;
    width: 100%;
    top: 50%;
    left: 0;
    margin-top: -1px;
    background: [[ closeButtonColor]];
    height: 1px;
}

.exponea-popup-banner .exponea-close .exponea-close-cross:before {
    -webkit-transform: rotate(45deg);
    transform: rotate(45deg);
}

.exponea-popup-banner .exponea-close .exponea-close-cross:after {
    -webkit-transform: rotate(45deg);
    transform: rotate(-45deg);
}

@media (max-width: 400px) {
    .exponea-popup-banner {
        width: 100%;
        max-width: none;
        bottom: 0;
        padding: 20px;
    }
    .exponea-popup-banner.exponea-animate {
        right: 0;
    }
}

.exponea-branding:hover,
.exponea-branding {
    font-size: 11px;
    position: absolute;
    color: [[buttonBackgroundColor]];
    opacity: 0.6;
    right: 10px;
    bottom: 10px;
    padding-top: 10px;
    text-decoration: none;
}

.exponea-branding:hover {
    text-decoration: none;
    opacity: 0.9;
}

script:
var self = this;
var showDelay = parseInt('[[ bannerShowDelayInMilliseconds ]]' || '0', 10);
setTimeout(function () {
    self.sdk.track('banner', getEventProperties('show', false));
    requestAnimationFrame(function () {
        document.body.insertAdjacentHTML('beforeend', self.html);
        var banner = self.banner = document.querySelector('.exponea-popup-banner');
        banner.insertAdjacentHTML('afterbegin', '<style>' + self.style + '</style>');
        var btnClose = banner.querySelector('.exponea-close');
        self.sdk.trackLink(banner, 'banner', getEventProperties('click'));
        requestAnimationFrame(function () {
            document.querySelector('.exponea-popup-banner').className += ' exponea-animate';
        });
        btnClose.onclick = function (e) {
            removeBanner();
            self.sdk.track('banner', getEventProperties('close'));
            e.preventDefault();
            if (e.stopPropagation) {
                e.stopPropagation();
            } else {
                e.cancelBubble = true;
            }
            return false;
        }
    });
}, showDelay);

function getEventProperties(action, interactive) {
    return {
        action: action,
        banner_id: self.data.banner_id,
        banner_name: self.data.banner_name,
        banner_type: self.data.banner_type,
        variant_id: self.data.variant_id,
        variant_name: self.data.variant_name,
        interaction: interactive !== false ? true : false,
        location: window.location.href,
        path: window.location.pathname
    };
}

function removeBanner() {
    if (self.banner) {
        self.banner.parentNode.removeChild(self.banner);
    }
}

return {
    remove: removeBanner
};

