var self = this;
var showDelay = parseInt('[[ bannerShowDelayInMilliseconds ]]' || '0', 10);
self.sdk.notifications.isAvailable(function (status) {
    if (status) {
        setTimeout(function () {
            self.sdk.track('banner', getEventProperties('show', false));
            requestAnimationFrame(function () {
                document.body.insertAdjacentHTML('beforeend', self.html);
                var banner = self.banner = document.querySelector('.exponea-popup-banner');
                banner.insertAdjacentHTML('afterbegin', '<style>' + self.style + '</style>');
                var btnClose = banner.querySelector('.exponea-close');
                self.sdk.trackLink(banner, 'banner', getEventProperties('click'));
                requestAnimationFrame(function () {
                    document.querySelector('.exponea-popup-banner').className += ' exponea-animate';
                });
                btnClose.onclick = function (e) {
                    removeBanner();
                    self.sdk.track('banner', getEventProperties('close'));
                    e.preventDefault();
                    if (e.stopPropagation) {
                        e.stopPropagation();
                    } else {
                        e.cancelBubble = true;
                    }
                    return false;
                };
                // notifications
                var enableButton = document.querySelector('.exponea-subscribe');
                var disableButton = document.querySelector('.exponea-unsubscribe');

                function updateStatus() {
                    self.sdk.notifications.isSubscribed(function (subscriptionActive, notificationsDenied) {
                        enableButton.className = enableButton.className.replace(/\b(disabled|hidden)\b/, '');
                        disableButton.className = disableButton.className.replace(/\b(disabled|hidden)\b/, '');
                        if (subscriptionActive) {
                            enableButton.className += ' hidden';
                        } else {
                            disableButton.className += ' hidden';
                        }
                        if (notificationsDenied) {
                            enableButton.className += ' disabled';
                            disableButton.className += ' disabled';
                        }
                    });
                };
                enableButton.onclick = function () {
                    enableButton.className += ' disabled';
                    self.sdk.notifications.subscribe(function (status) {
                        updateStatus();
                    });
                    return false;
                };
                disableButton.onclick = function () {
                    disableButton.className += ' disabled';
                    self.sdk.notifications.unsubscribe(function (status) {
                        updateStatus();
                    });
                    return false;
                };
                updateStatus();
            });
        }, showDelay);
    }
});

function getEventProperties(action, interactive) {
    return {
        action: action,
        banner_id: self.data.banner_id,
        banner_name: self.data.banner_name,
