                            enableButton.className = enableButton.className.replace(/\b(disabled|hidden)\b/,'');
                            disableButton.className = disableButton.className.replace(/\b(disabled|hidden)\b/,'');
                            if (subscriptionActive) {
                              enableButton.className += ' hidden';
                            } else {
                              disableButton.className += ' hidden';
                            }
                            if (notificationsDenied) {
                              enableButton.className += ' disabled';
                              disableButton.className += ' disabled';
                            }
                          });
                        };
                        enableButton.onclick=function(){
                          enableButton.className += ' disabled';
