                          });
                          return false;
                        };
                        disableButton.onclick=function(){
                          disableButton.className += ' disabled';
                          self.sdk.notifications.unsubscribe(function(status){
                            updateStatus();
                          });
                          return false;
                        };
                        updateStatus();
                      });
                    }, showDelay);
                  }
