parametrized_parts.script:
var self = this;
var now = Date.parse(new Date());
var timestamp = localStorage.getItem('__exp_timer');
var interval;

if (!storageAvailable('localStorage')) {
    return;
}

requestAnimationFrame(function () {
    if (self.inPreview) { 	/*Code for preview mode*/
        localStorage.setItem('__exp_timer', now);
        renderBanner();
    }else { 
    	document.querySelector('.default').addEventListener('click', function (){
			renderBanner();
		});
		
		if (timestamp){
			if (parseInt(timestamp) < (parseInt(now) - parseInt('1200000'))) {
				trackTimeout();
			    return;
			}
			renderBanner();
		}
    }
});

function renderBanner(){
	requestAnimationFrame(function () {
	    if (!timestamp) {
	        timestamp = Date.parse(new Date());
	        localStorage.setItem('__exp_timer', timestamp);
	    }
	    document.body.insertAdjacentHTML('afterbegin', self.html);
	    var banner = self.banner = document.querySelector('.exponea-banner');
	    banner.insertAdjacentHTML('afterbegin', '<style>' + self.style + '</style>');
	    self.sdk.track('banner', getEventProperties('show', false));
	    interval = setInterval(function () {
	        now = Date.parse(new Date());
	        var timeString;
	        if (parseInt(timestamp) >= (parseInt(now) - parseInt('1200000'))) {
	            timeString = sec2str((parseInt(timestamp) + parseInt('1200000') - parseInt(now)) / 1000);
	            banner.querySelector('span.exponea-timer').innerHTML = timeString;
	            requestAnimationFrame(function () {
	                document.querySelector('.exponea-banner').style.display = "block";
	            });
	        }
	        if (timeString == "00:00") {
	            trackTimeout();
	            removeBanner();
	        }
	    }, 1000);
	
	    var btnClose = banner.querySelector('.exponea-close');
	    self.sdk.trackLink(banner, 'banner', getEventProperties('click'));
	    btnClose.onclick = function (e) {
	        removeBanner();
	        self.sdk.track('banner', getEventProperties('close'));
	        e.preventDefault();
	        if (e.stopPropagation) {
	            e.stopPropagation();
	        } else {
	            e.cancelBubble = true;
	        }
	        return false;
	    }
	});
}

function trackTimeout(){
	if (!localStorage.getItem('__exp_timer_timeout')){
		self.sdk.track('banner', getEventProperties('timeout'));
		localStorage.setItem('__exp_timer_timeout',"timeout");
	}
}
function getEventProperties(action, interactive) {
    return {
        action: action,
        banner_id: self.data.banner_id,
        banner_name: self.data.banner_name,
        banner_type: self.data.banner_type,
        variant_id: self.data.variant_id,
        variant_name: self.data.variant_name,
        interaction: interactive !== false ? true : false,
        location: window.location.href,
        path: window.location.pathname
    };
}

function removeBanner() {
    if (self.banner) {
        self.banner.parentNode.removeChild(self.banner);
        clearTimeout(interval);
    }
}
return {
    remove: removeBanner
};

function storageAvailable(type) {
    try {
        var storage = window[type],
            x = '__storage_test__';
        storage.setItem(x, x);
        storage.removeItem(x);
        return true;
    } catch (e) {
        return false;
    }
}

function sec2str(t) {
    var d = Math.floor(t / 86400),
        h = ('0' + Math.floor(t / 3600) % 24).slice(-2),
        m = ('0' + Math.floor(t / 60) % 60).slice(-2),
        s = ('0' + t % 60).slice(-2);
    return (d > 0 ? d + 'days ' : '') + (h > 0 ? h + ':' : '') + (m > 0 ? m + ':' : '00:') + (t > 60 ? s : s + '');
}

