function countdown(datestring, target_id) {
  var end = new Date(datestring);
  var _second = 1000;
  var _minute = _second * 60;
  var _hour = _minute * 60;
  var _day = _hour * 24;
  var timer;
  function showRemaining() {
    var now = new Date();
    var distance = end.getTime() - now.getTime();
    if (distance < 0) {
      clearInterval(timer);
      document.getElementById(target_id).innerHTML = 'Ponuka skončila';
      return;
    }
    var time = {};
    time.days = Math.floor(distance / _day);
    time.hours = Math.floor((distance % _day) / _hour);
    time.minutes = Math.floor((distance % _hour) / _minute);
    time.seconds = Math.floor((distance % _minute) / _second);
    document.getElementById(target_id).innerHTML = 'Ponuka končí o  ' + localizeTime(time, 'slovak') + '.';
  }  
  timer = setInterval(showRemaining, 1000);
  showRemaining();
}
var self = this;
var showDelay = parseInt('2000' || '0', 10);
setTimeout(function() {
  self.sdk.track('banner', getEventProperties('show', false));
  requestAnimationFrame(function() {
    document.body.insertAdjacentHTML('beforeend', self.html);
    var banner = self.banner = document.querySelector('.exponea-leaderboard');
    banner.insertAdjacentHTML('afterbegin', '<style>' + self.style + '</style>');
    var btnClose = banner.querySelector('.exponea-close');
    self.sdk.trackLink(banner, 'banner', getEventProperties('click'));
    requestAnimationFrame(function() {
      countdown('2017-08-30T19:00:00', 'target'); /* input - EDIT UTC TIMESTAMP HERE */
      // day, month, year, hour, minute, target element id
      document.querySelector('.exponea-leaderboard').className += ' exponea-animate';
    });
    btnClose.onclick = function(e) {
      removeBanner();
      self.sdk.track('banner', getEventProperties('close'));
      e.preventDefault();
      if (e.stopPropagation) {
        e.stopPropagation();
      } else {
        e.cancelBubble = true;
      }
      return false;
    };
  });
}, showDelay);
function getEventProperties(action, interactive) {
  return {
    action: action,
    banner_id: self.data.banner_id,
    banner_name: self.data.banner_name,
    banner_type: self.data.banner_type,
    variant_id: self.data.variant_id,
    variant_name: self.data.variant_name,
    interaction: interactive !== false ? true : false,
    location: window.location.href,
    path: window.location.pathname
  };
}
function removeBanner() {
  if (self.banner) {
    self.banner.parentNode.removeChild(self.banner);
  }
}
return {
  remove: removeBanner
};

function localizeTime(time, language){
	var data = {};
	var string = '';
	switch(language){
		case 'slovak':
			data = slovak_local();
			break;
		case 'czech':
			data = czech_local();
			break;
		case 'polish':
			data = polish_local();
			break;			
		default:
			data = english_local();
	}
	if(time.days > 0){
		string += (time.days + ' ' + findTime(time.days, data.days) + ', ');
	}
	if(time.hours > 0 || time.days > 0){
		string += (time.hours + ' ' + findTime(time.hours, data.hours) + ', ');
	}
	if(time.hours > 0 || time.days > 0 || time.minutes > 0){
		string += (time.minutes + ' ' + findTime(time.minutes, data.minutes) + data.conj);
	}	
	if(time.hours > 0 || time.days > 0 || time.minutes > 0 || time.seconds > 0){
		string += (time.seconds + ' ' + findTime(time.seconds, data.seconds));
	}	
	return string;
}

function findTime(time, data){
	var i = 0;
	while(i < data.length && data[i].limit != -1 && data[i].limit < time){
		i++;
	}
	if(i >= data.length) return '';
	return data[i].string;
}

