script:
var self = this;
                var showDelay = parseInt('[[ bannerShowDelayInMilliseconds ]]' || '0', 10);
                setTimeout(function() {
                    self.sdk.track('banner', getEventProperties('show', false));
                    requestAnimationFrame(function () {
                        document.body.insertAdjacentHTML('beforeend', self.html);
                        var banner = self.banner = document.querySelector('.exponea-survey');
                        var starsWrapper = banner.querySelector('.exponea-survey .stars');
                        var stars = starsWrapper.querySelectorAll('.positive, .negative');
                        var form = banner.querySelector('form');
                        form.onsubmit = function () {
                            removeBanner();
                            self.sdk.track('banner', getEventProperties('survey'));
                            var properties = getEventProperties('survey');
                            properties.question = banner.querySelector('.exponea-question').textContent;
                            properties.answer = form.answer.value;
                            self.sdk.track('survey', properties);
                            return false;
                        }
                        starsWrapper.onmouseleave = function () {
                            var lastResponse = parseInt(form.answer.value);
                            for (var i = 1; i <= stars.length; i++) {
                                stars[i - 1].className = (i <= lastResponse) ? 'positive' : 'negative';
                            }
                        }
                        for (var i = 1; i <= stars.length; i++) {
                            var star = stars[i - 1];
                            star.onmouseover = function () {
                                elem = this;
                                while (elem.previousSibling) {
                                    elem.className = 'positive';
                                    elem = elem.previousSibling;
                                }
                                elem = this.nextSibling;
                                while (elem.nextSibling) {
                                    elem.className = 'negative';
                                    elem = elem.nextSibling;
                                }
                            };
                            star.onclick = (function (response) {
                                return function () {
                                    form.answer.value = response;
                                };
                            })(i);
                        }
                        banner.insertAdjacentHTML('afterbegin', '<style>' + self.style + '</style>');
                        var btnClose = banner.querySelector('.exponea-close');
                        btnClose.onclick = function () {
                            removeBanner();
                            self.sdk.track('banner', getEventProperties('close'));
                        };
                        requestAnimationFrame(function () {
                            document.querySelector('.exponea-survey').className += ' exponea-animate';
                        });
                    });
                }, showDelay);
                function getEventProperties(action, interactive) {
                    return { action: action, banner_id: self.data.banner_id, banner_name: self.data.banner_name, banner_type: self.data.banner_type, variant_id: self.data.variant_id, variant_name: self.data.variant_name, interaction: interactive !== false ? true : false, location: window.location.href, path: window.location.pathname };
                }
                function removeBanner() {
                    if (self.banner) {
                        self.banner.parentNode.removeChild(self.banner);
                    }
                }
                return {
                    remove: removeBanner
                };

