.exponea-popup-banner {
box-sizing: border-box;
 display: block;
 position: fixed;
 bottom: 15px;
 right: -360px;
 max-width: 370px;
 z-index: 9999999;
 background: #ffd500;
 transition: right 1.5s;
 font-family: Arial, Helvetica, sans-serif;
 text-align: center;
 line-height: normal;
 text-decoration: inherit;
 cursor: default;
 padding: 35px;
}

.exponea-popup-banner:hover, .exponea-popup-banner:active {
text-decoration: inherit;
}

.exponea-popup-banner.exponea-animate {
right: 15px;
}

.exponea-popup-banner span {
display: block;
}

.exponea-popup-banner span.disabled {
background-color: gray;
 cursor: default;
}

.exponea-popup-banner span.hidden {
display:none;
}

.exponea-popup-banner .exponea-header {
font-size: 10px;
 font-weight: bold;
 text-transform: uppercase;
 color: #1c1733;
 padding: 15px;
}

.exponea-popup-banner .exponea-text {
font-size: 24px;
 font-weight: 600;
 color: #ffffff;
 margin-bottom: 32px 
}

.exponea-popup-banner .exponea-button {
display: inline-block;
 font-size: 14px;
 color: #ffffff;
 background: #1c1733;
 padding: 13px 37px;
 border-radius: 50px;
 -moz-border-radius: 50px;
 -webkit-border-radius: 50px;
 border: 0px solid #800000;
 cursor: pointer;
}

.exponea-popup-banner .exponea-close {
padding: 15px;
 position: absolute;
 top: 0px;
 right: 0px;
}

.exponea-popup-banner .exponea-close .exponea-close-cross {
position: absolute;
 display: inline-block;
 width: 14px;
 height: 14px;
 overflow: hidden;
 top: 8px;
 right: 8px;
}

.exponea-popup-banner .exponea-close:hover {
cursor: pointer;
}

.exponea-popup-banner .exponea-close .exponea-close-cross:before, .exponea-popup-banner .exponea-close .exponea-close-cross:after {
  content: '';
 position: absolute;
