var self = this;
                var showDelay = parseInt('[[ bannerShowDelayInMilliseconds ]]' || '0', 10);
                self.timeinterval = NaN;
                function getTimeRemaining(endtime) {
                    var t = Math.max(endtime - new Date(), 0);
                    var seconds = Math.floor((t / 1000) % 60);
                    var minutes = Math.floor((t / 1000 / 60) % 60);
                    var hours = Math.floor((t / (1000 * 60 * 60)) % 24);
                    var days = Math.floor(t / (1000 * 60 * 60 * 24));
                    return {
                        total: t,
                        days: days,
                        hours: hours,
                        minutes: minutes,
                        seconds: seconds
                    };
                }
                function initializeClock(banner, endtime) {
                    var clock = banner.querySelector('.exponea-countdown-timer');
                    var daysSpan = clock.querySelector('.days');
                    var hoursSpan = clock.querySelector('.hours');
                    var minutesSpan = clock.querySelector('.minutes');
                    var secondsSpan = clock.querySelector('.seconds');
                    function updateClock() {
                        var t = getTimeRemaining(endtime);
                        daysSpan.innerHTML = t.days;
                        hoursSpan.innerHTML = ('0' + t.hours).slice(-2);
                        minutesSpan.innerHTML = ('0' + t.minutes).slice(-2);
                        secondsSpan.innerHTML = ('0' + t.seconds).slice(-2);
                        if (t.total <= 0 && self.inPreview !== true) {
                            removeBanner();
                        }
                    }
                    updateClock();
                    self.timeinterval = setInterval(updateClock, 1000);
                }
                setTimeout(function () {
                    var endTimeString = '[[ endTime ]]';
                    var endTimeDate = new Date(Date.parse(endTimeString));
                    if (endTimeDate > new Date() || self.inPreview === true) {
                        requestAnimationFrame(function () {
                            document.body.insertAdjacentHTML('beforeend', self.html);
                            var banner = self.banner = document.querySelector('.exponea-countdown');
                            banner.insertAdjacentHTML('afterbegin', '<style>' + self.style + '</style>');
                            requestAnimationFrame(function () {
                                banner.className += ' exponea-animate';
                                self.sdk.track('banner', getEventProperties('show', false));
                            });
                            initializeClock(banner, endTimeDate);
                            var bannerButton = banner.querySelector('.exponea-button');
                            self.sdk.trackLink(bannerButton, 'banner', getEventProperties('click'));
                            var buttonClose = banner.querySelector('.exponea-close');
                            buttonClose.onclick = function () {
                                removeBanner();
                                self.sdk.track('banner', getEventProperties('close'));
                                return false;
                            };
                        });
                    }
                }, showDelay);
                function getEventProperties(action, interactive) {
                    return {
                        action: action,
                        banner_id: self.data.banner_id,
                        banner_name: self.data.banner_name,
                        banner_type: self.data.banner_type,
                        variant_id: self.data.variant_id,
                        variant_name: self.data.variant_name,
                        interaction: interactive !== false ? true : false,
                        location: window.location.href,
                        path: window.location.pathname
                    };
                }
                function removeBanner() {
                    if (self.banner) {
                        self.banner.parentNode.removeChild(self.banner);
                    }
                    if (!isNaN(self.timeinterval)) {
                        clearInterval(self.timeinterval);
                        self.timeinterval = NaN;
                    }
                }
                return {
                    remove: removeBanner
                };

