  text-align: left;
  line-height: normal;
  text-decoration: inherit;
  cursor: pointer;
  padding: 10px;
  padding-right:30px;
  border-radius:6px;
}
.exponea-leaderboard, .exponea-leaderboard * {
  box-sizing: content-box;
}
.exponea-leaderboard:hover, .exponea-leaderboard:active {
  text-decoration: inherit;
}
.exponea-leaderboard.exponea-animate {
  top: 15px;
}
.exponea-leaderboard span {
  display: block;
}
.exponea-leaderboard .exponea-text {
  font-size: 15px;
  padding-left:10px;
  font-weight: normal;
  color: [[ notificationTextColor ]];
}
.exponea-leaderboard .exponea-close {
  padding: 15px;
  position: absolute;
  top: -5px;
  right: -5px;
}
.exponea-leaderboard .exponea-close .exponea-close-cross {
  position: absolute;
  display: inline-block;
  width: 10px;
  height: 10px;
  overflow: hidden;
  top: 10px;
  right: 10px;
}
.exponea-leaderboard .exponea-close:hover {
  cursor: pointer;
}
.exponea-leaderboard .exponea-close .exponea-close-cross:before, .exponea-leaderboard .exponea-close .exponea-close-cross:after {
  content: '';
  position: absolute;
  height: 2px;
  width: 100%;
  top: 50%;
  left: 0;
  margin-top: -1px;
  background: [[ closeButtonColor ]];
  height: 1px;
}
.exponea-leaderboard .exponea-close .exponea-close-cross:before {
  -webkit-transform: rotate(45deg);
  transform: rotate(45deg);
}
.exponea-leaderboard .exponea-close .exponea-close-cross:after {
  -webkit-transform: rotate(45deg);
  transform: rotate(-45deg);
}
