var self = this;
var showDelay = parseFloat('[[ bannerShowDelayInSeconds | 5 ]]', 10);

if (self.inPreview) {
    // reset the delay while editing the banner in Exponea
    showDelay = 0;
}

setTimeout(function() {
    self.sdk.track('banner', getEventProperties('show', false));

    requestAnimationFrame(function() {
        self.sdk.notifications.isSubscribed(function(subscriptionActive, notificationsDenied) {
            // display banner only if user/device is not already subscribed
            if (!subscriptionActive && !notificationsDenied) {

                // insert HTML
                document.body.insertAdjacentHTML('afterbegin', self.html);

                var banner = (self.banner = document.querySelector('.exponea-web-notifications'));
                var btnClose = banner.querySelector('.exponea-web-notifications-close');
                var btnNo = banner.querySelector('.exponea-banner-secondary');
                var btnYes = banner.querySelector('.exponea-banner-primary');

                // insert CSS
                banner.insertAdjacentHTML(
                    'afterbegin',
                    '<style>' + self.style + '</style>'
                );

                banner.classList.add('animate');

                self.sdk.trackLink(banner, 'banner', getEventProperties('click'));

                btnClose.onclick = function(e) {
                    removeBanner();
                    self.sdk.track('banner', getEventProperties('close'));
                };

                btnNo.onclick = function(e) {
                    removeBanner();
                    self.sdk.track('banner', getEventProperties('refuse'));
                };

                btnYes.onclick = function(e) {
                    removeBanner();
                    //self.sdk.track('banner', getEventProperties('accept'));

                    self.sdk.notifications.subscribe(function(status){
                        self.sdk.notifications.isSubscribed(function(subscriptionActive, notificationsDenied) {
                            if (subscriptionActive) {
                                self.sdk.track('banner', getEventProperties('subscribe'));
                            }
                        });
                    });
                };
            }
        });
    });
}, showDelay);

function getEventProperties(action, interactive) {
    return {
        action: action,
        banner_id: self.data.banner_id,
        banner_name: self.data.banner_name,
        banner_type: self.data.banner_type,
        variant_id: self.data.variant_id,
        variant_name: self.data.variant_name,
        interaction: interactive !== false,
        location: window.location.href,
        path: window.location.pathname
    };
}

function removeBanner() {
    if (self.banner && self.banner.parentNode) {
        self.banner.parentNode.removeChild(self.banner);
    }
}

return {
    remove: removeBanner
};

